import * as R from 'ramda';
import comics from './mockComics';
import request from 'superagent';

export const fetchComics = async () => {
    return new Promise(resolve => {
        resolve(comics)
    })
}

export const fetchComicsById = async id => {
    return new Promise(resolve => {
        const comicsBook = R.find(R.propEq('id', id), comics)    
        resolve(comicsBook)
    })
}

export const fetchCategories = async () => {
    const {body} = await request.get('http://localhost:3001/categories')
    return body;
}
