import './basketCart.css'
import React, {Component} from 'react'
import {Link} from 'react-router'
import {connect} from 'react-redux'
import { getTotalBasketCount } from 'selectors';


const BasketCart = ({length}) => {
    

    return (
            <Link
                to={`/basket`}
            >
                <div className="basketCart">
                    <img
                        className='basketCart__bth'
                        src="/icons/basketCart.png"
                    />
                    <div className="basketCart__count">
                        {length}
                    </div>
                </div>
            </Link>
        )
}
const mapStateToProps = state => ({
    length: getTotalBasketCount(state),
 })
 

 
 export default connect(mapStateToProps, null)(BasketCart)

