import './header.css'
import React, {Component} from 'react'
import {Link} from 'react-router'
import { connect } from 'react-redux'
import PropTypes from 'prop-types'

import BasketCart from 'components/basketCart';
import UserButtonSignUp from 'components/signup';
import UserButtonSignIn from 'components/signin';
import UserButtonLogOut from 'components/logout';

class Header extends Component {
    constructor (props) {
        super(props)
    }

    render () {
        const {isAuthenticated} = this.props.user
        return (
            <div className="header">
                <div className="head-line">
                    <Link
                        to={`/`}
                        className='logo'
                    >
                        Vertigo
                    </Link>
                    <div className="menu">
                    {isAuthenticated ? <UserButtonLogOut/> : 
                        <div className="auth"> 
                            <UserButtonSignUp/>  
                            <UserButtonSignIn/> 
                        </div>} 
                        <BasketCart/>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    user: state.user
})
  
  export default connect(mapStateToProps, {})(Header)
