// Imports
import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import PropTypes from 'prop-types'

import { userLogout } from 'actions'

class UserButtonLogOut extends Component {
    constructor () {
        super()
        this.state = {
        notification: false,
        loggedOut: false
        }
    }

    logout (event) {
        event.preventDefault()
        this.props.userLogout()
    }

  render () {
    return (
      <button className="auth__btn" onClick={this.logout.bind(this)}>
       Выйти
      </button>
    )
  }
}

UserButtonLogOut.propTypes = {
  userLogout: PropTypes.func.isRequired,
}

export default connect(null, {userLogout})(UserButtonLogOut)