import { USER_CURRENT_SET } from 'actionTypes'

const initialState = {
  isAuthenticated: false,
  user: {}
}

export default (state = initialState, action = {}) => {
  switch (action.type) {
    case USER_CURRENT_SET:
      return {
        isAuthenticated: Object.keys(action.user).length,
        user: action.user
      }
    default:
      return state
  }
}